entry.value:
import org.apache.nifi.processor.FlowFileFilter
import org.apache.commons.io.IOUtils
import java.nio.charset.StandardCharsets
import groovy.json.JsonSlurper
import groovy.json.JsonBuilder
import groovy.json.JsonOutput


def flowFile = session.get()
if(!flowFile) return

def errorMessage = null

def outValue = ""
def outputRelation = REL_SUCESS

def slurper = new JsonSlurper()

flowFile = session.write(flowFile, {inputStream, outputStream ->
	try{
		def text = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
		def someJsonObj = slurper.parseText(text)
		def modifiedSomeJsonObj = [:]

		someJsonObj.each{k,v ->
			if(k == "createdTime"){
				modifiedSomeJsonObj.createdTime = "$v"
			}

			if(k == "Description"){
				def ml = v[0]
				ml.each{k1,v1->
					if(k1 == "Value"){
							modifiedSomeJsonObj.Description = "$v1"
					}

				}
			}

		}
	}catch(Exception e){
		outputRelation = e
	}

}as StreamCallback)

if(errorMessage != null){
	flowFile = session.putAttribute(flowFile, "error", "Error Parsing")
}else{
	flowFile = session.putAttribute(flowFile, "success", "merged json data")
}

session.transfer(flowFile, outputRelation)
